command:
const difficultyChoices = [
  { key: "2d8", label: "GUBAT_BANWA.Rolls.Difficulty.Risky" },
  { key: "4d8", label: "GUBAT_BANWA.Rolls.Difficulty.Difficult" },
  { key: "6d8", label: "GUBAT_BANWA.Rolls.Difficulty.Formidable" },
  { key: "8d8", label: "GUBAT_BANWA.Rolls.Difficulty.Impossible" },
];

const content = await renderTemplate(
  "systems/gubat-banwa-1-4/templates/dialog/dialog-difficulty.hbs",
  { difficultyChoices }
);

const difficulty = await Dialog.wait({
  title: game.i18n.localize("GUBAT_BANWA.Categories.Difficulty"),
  content,
  buttons: {
    select: {
      label: game.i18n.localize("GUBAT_BANWA.UI.Select"),
      icon: '<i class="fas fa-check"></i>',
      callback: async (html) => {
        const formData = new FormDataExtended(
          html[0].querySelector("form#dialog-difficulty")
        ).object;
        return formData.difficulty;
      },
    },
    cancel: {
      label: game.i18n.localize("GUBAT_BANWA.UI.Cancel"),
      icon: '<i class="fas fa-xmark"></i>',
    },
  },
});

let roll = new Roll(`${difficulty}cs>=6`, {});
const rollData = await roll.evaluate();
ChatMessage.create({
  speaker: ChatMessage.getSpeaker(),
  content: await renderTemplate(
    "systems/gubat-banwa-1-4/templates/chat/chat-roll.hbs",
    {
      ...rollData.dice[0],
      total: rollData.total,
      statLabel: "GUBAT_BANWA.Categories.Difficulty",
    },
  ),
  type: CONST.CHAT_MESSAGE_TYPES.ROLL,
  rolls: [roll],
});
